// src/components/RecruiterDashboard.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RecruiterDashboard = () => {
  const [jobs, setJobs] = useState([]);
  const [newJob, setNewJob] = useState({
    title: '',
    description: '',
    location: '',
    skills: '',
  });

  // Fetch jobs posted by recruiter
  const fetchJobs = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/jobs/recruiter', {
        withCredentials: true
      });
      setJobs(response.data);
    } catch (error) {
      console.error('Error fetching jobs:', error);
    }
  };

  useEffect(() => {
    fetchJobs();
  }, []);

  // Handle job form change
  const handleChange = (e) => {
    setNewJob({
      ...newJob,
      [e.target.name]: e.target.value
    });
  };

  // Post new job
  const handlePostJob = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/jobs', newJob, {
        withCredentials: true
      });
      setJobs([...jobs, response.data]);
      setNewJob({ title: '', description: '', location: '', skills: '' });
    } catch (error) {
      console.error('Error posting job:', error.response ? error.response.data : error);
    }
  };

  return (
    <div className="dashboard">
      <h2>Recruiter Dashboard</h2>

      {/* Post Job Form */}
      <div className="post-job">
        <h3>Post a New Job</h3>
        <input
          type="text"
          name="title"
          placeholder="Job Title"
          value={newJob.title}
          onChange={handleChange}
        />
        <textarea
          name="description"
          placeholder="Job Description"
          value={newJob.description}
          onChange={handleChange}
        />
        <input
          type="text"
          name="location"
          placeholder="Location"
          value={newJob.location}
          onChange={handleChange}
        />
        <input
          type="text"
          name="skills"
          placeholder="Required Skills (comma separated)"
          value={newJob.skills}
          onChange={handleChange}
        />
        <button onClick={handlePostJob}>Post Job</button>
      </div>

      {/* Jobs List */}
      <div className="jobs-list">
        <h3>Your Posted Jobs</h3>
        {jobs.map(job => (
          <div key={job.id} className="job-card">
            <h4>{job.title}</h4>
            <p>{job.description}</p>
            <p><strong>Location:</strong> {job.location}</p>
            <p><strong>Skills:</strong> {job.skills}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RecruiterDashboard;
