import React, { useState, useEffect } from 'react';
import api from '../api/axiosConfig';

export default function RecruiterDashboard({ user }) {
  const [job, setJob] = useState({
    title: '',
    description: '',
    location: '',
    skills: ''
  });

  const [myJobs, setMyJobs] = useState([]);
  const [users, setUsers] = useState([]);

  // Fetch jobs posted by this recruiter
  async function fetchMyJobs() {
    try {
      const res = await api.get('/jobs/myjobs', { withCredentials: true });
      setMyJobs(res.data);
    } catch (err) {
      console.error('Failed to fetch jobs:', err);
    }
  }

  // Fetch all users (to display applicant details)
  async function fetchUsers() {
    try {
      const res = await api.get('/api/admin/users'); // or your endpoint to get users
      setUsers(res.data);
    } catch (err) {
      console.error('Failed to fetch users:', err);
    }
  }

  useEffect(() => {
    fetchMyJobs();
    fetchUsers();
  }, []);

  const handleChange = (e) => {
    setJob({ ...job, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post('/jobs/create', job, { withCredentials: true });
      alert('Job created successfully!');
      setJob({ title: '', description: '', location: '', skills: '' });
      fetchMyJobs(); // Refresh job list
    } catch (err) {
      console.error('Error creating job:', err);
      alert('Failed to create job. Are you logged in as a recruiter?');
    }
  };

  // Download applicant resume by userId
  const handleDownloadResume = async (userId) => {
    try {
      const response = await fetch(`/profile/resume/${userId}`, {
        credentials: 'include',
      });
      if (!response.ok) throw new Error('Failed to download resume');

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;

      // Extract filename from content-disposition header if possible
      const disposition = response.headers.get('Content-Disposition');
      let filename = 'resume.pdf';
      if (disposition && disposition.includes('filename=')) {
        filename = disposition
          .split('filename=')[1]
          .split(';')[0]
          .replace(/"/g, '');
      }

      a.download = filename;
      document.body.appendChild(a);
      a.click();
      a.remove();
      window.URL.revokeObjectURL(url);
    } catch (error) {
      alert('Download failed: ' + error.message);
    }
  };

  // Helper to get applicant details by userId
  function getApplicantDetails(userId) {
    return users.find(u => u.id === userId);
  }

  return (
    <div>
      <h2>Post a New Job</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="title"
          placeholder="Job Title"
          onChange={handleChange}
          value={job.title}
          required
        />
        <textarea
          name="description"
          placeholder="Job Description"
          onChange={handleChange}
          value={job.description}
          required
        />
        <input
          type="text"
          name="location"
          placeholder="Location"
          onChange={handleChange}
          value={job.location}
          required
        />
        <input
          type="text"
          name="skills"
          placeholder="Skills (comma separated)"
          onChange={handleChange}
          value={job.skills}
          required
        />
        <button type="submit">Create Job</button>
      </form>

      <h2>My Posted Jobs</h2>
      {myJobs.length === 0 && <p>No jobs posted yet.</p>}

      {myJobs.map(job => (
        <div key={job.id} style={{ border: '1px solid #ccc', marginTop: 10, padding: 10 }}>
          <h3>{job.title}</h3>
          <p>{job.description}</p>
          <p><b>Location:</b> {job.location}</p>
          <p><b>Skills:</b> {job.skills}</p>

          <h4>Applicants:</h4>
          {(!job.applicantIds || job.applicantIds.length === 0) && <p>No applicants yet.</p>}

          <ul>
            {(job.applicantIds || []).map(userId => {
              const applicant = getApplicantDetails(userId);
              return (
                <li key={userId}>
                  {applicant
                    ? `${applicant.fullName || applicant.username} (${applicant.email || 'N/A'})`
                    : 'Applicant info not found'}
                  {' '}
                  <button onClick={() => handleDownloadResume(userId)}>
                    Download Resume
                  </button>
                </li>
              );
            })}
          </ul>
        </div>
      ))}
    </div>
  );
}
