import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../api';

function Navbar({ user, setUser }) {
  const navigate = useNavigate();

  const logout = async () => {
    try {
      await api.get('/api/auth/logout');
    } catch (e) {
      // ignore errors, still clear UI state
    }
    setUser(null);
    navigate('/login');
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light">
      <div className="container-fluid">
        <Link className="navbar-brand" to="/">JobPortal</Link>
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#nav">
          <span className="navbar-toggler-icon" />
        </button>

        <div className="collapse navbar-collapse" id="nav">
          <ul className="navbar-nav me-auto mb-2 mb-lg-0">
            <li className="nav-item">
              <Link className="nav-link" to="/">Jobs</Link>
            </li>
            {user && user.role === 'ADMIN' && (
              <li className="nav-item">
                <Link className="nav-link" to="/admin">Admin</Link>
              </li>
            )}
            {user && user.role === 'RECRUITER' && (
              <li className="nav-item">
                <Link className="nav-link" to="/recruiter">Recruiter</Link>
              </li>
            )}
            {user && user.role === 'JOBSEEKER' && (
              <>
                <li className="nav-item">
                  <Link className="nav-link" to="/jobseeker">Dashboard</Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link" to="/profile">Profile</Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link" to="/resume">Resume</Link>
                </li>
              </>
            )}
          </ul>

          <ul className="navbar-nav ms-auto">
            {user ? (
              <>
                <li className="nav-item nav-link">{user.username} ({user.role})</li>
                <li className="nav-item">
                  <button className="btn btn-outline-secondary" onClick={logout}>Logout</button>
                </li>
              </>
            ) : (
              <>
                <li className="nav-item">
                  <Link className="nav-link" to="/login">Login</Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link" to="/register">Register</Link>
                </li>
              </>
            )}
          </ul>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
