import React from 'react';

export default function JobCard({ job, onDelete, showDelete }) {
  return (
    <div className="card" style={{marginBottom:12}}>
      <div style={{display:'flex',justifyContent:'space-between'}}>
        <div>
          <h3 style={{margin:'0 0 6px 0'}}>{job.title}</h3>
          <div className="small">{job.location} â€¢ Skills: {job.skills}</div>
          <p style={{marginTop:8}}>{job.description}</p>
        </div>
        {showDelete && (
          <div>
            <button onClick={() => onDelete(job.id)} className="btn">Delete</button>
          </div>
        )}
      </div>
    </div>
  );
}
