import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api/axiosConfig';
import './Login.css';

export default function Login({ setUser }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('Your session expired — please log in again.');
  const navigate = useNavigate();

  async function submit(e) {
    e.preventDefault();
    setError('');
    try {
      const res = await api.post('/api/auth/login', { username, password }, { withCredentials: true });
      setUser(res.data);
      if (res.data.role === 'ADMIN') navigate('/admin');
      else if (res.data.role === 'RECRUITER') navigate('/recruiter');
      else navigate('/');
    } catch (err) {
      setError(err.response?.data || 'Login failed. Please try again.');
    }
  }

  return (
    <div className="login-container">
      <h1 className="login-title">Login</h1>
      {error && <div className="session-message">{error}</div>}

      <form onSubmit={submit} className="login-form">
        <div className="form-group">
          <label htmlFor="username">Username</label>
          <input
            id="username"
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            id="password"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        <button type="submit" className="login-button">Login</button>
      </form>
    </div>
  );
}
