import React, { useEffect, useState } from 'react';
import api from '../api';
import JobCard from './components/JobCard';

function JobList({ user }) {
  const [jobs, setJobs] = useState([]);
  const [query, setQuery] = useState('');
  const [filterType, setFilterType] = useState('title');

  const fetchJobs = async (q) => {
    try {
      let url = '/jobs/all';
      if (q) url = `/jobs/filter?${filterType}=${encodeURIComponent(q)}`;
      const res = await api.get(url);
      setJobs(res.data);
    } catch (e) {
      console.error(e);
    }
  };

  useEffect(() => { fetchJobs(); }, []);

  const doSearch = (e) => {
    e.preventDefault();
    fetchJobs(query);
  };

  return (
    <div>
      <h3>Available Jobs</h3>
      <form className="row g-2 mb-3" onSubmit={doSearch}>
        <div className="col-md-3">
          <select className="form-select" value={filterType} onChange={e => setFilterType(e.target.value)}>
            <option value="title">Title</option>
            <option value="location">Location</option>
            <option value="skill">Skill</option>
          </select>
        </div>
        <div className="col-md-6">
          <input className="form-control" placeholder={`Search by ${filterType}`} value={query} onChange={e => setQuery(e.target.value)} />
        </div>
        <div className="col-md-3">
          <button className="btn btn-primary">Search</button>
        </div>
      </form>

      <div className="row">
        {jobs.map(job => (
          <div className="col-md-4" key={job.id}>
            <div className="card mb-3">
              <div className="card-body">
                <h5 className="card-title">{job.title}</h5>
                <h6 className="card-subtitle mb-2 text-muted">{job.location}</h6>
                <p className="card-text">{job.description}</p>
                <p><strong>Skills:</strong> {job.skills}</p>
                <p><small>Posted by: {job.postedBy?.username || 'Unknown'}</small></p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default JobList;
