import React, { useEffect, useState } from 'react';
import api from '../api/axiosConfig';
import CreateJob from './CreateJob';
import JobCard from '../components/JobCard';

export default function RecruiterDashboard({ user }){
  const [myJobs, setMyJobs] = useState([]);

  async function fetchMyJobs(){
    try{ const res = await api.get('/jobs/my-jobs'); setMyJobs(res.data); }catch(e){console.error(e)}
  }
  useEffect(()=>{ fetchMyJobs(); }, []);

  async function handleDelete(id){ if(!window.confirm('Delete job?')) return; await api.delete('/jobs/delete/'+id); setMyJobs(myJobs.filter(j=>j.id!==id)); }

  return (
    <div className="container">
      <div className="card">
        <h2>Recruiter Dashboard</h2>
        <CreateJob onCreated={fetchMyJobs} />
        <h3 style={{marginTop:12}}>My Jobs</h3>
        {myJobs.map(j=> <JobCard key={j.id} job={j} onDelete={handleDelete} showDelete={true} />)}
      </div>
    </div>
  );
}
