import React, { useState } from 'react';
import api from '../services/api';

export default function JobPostForm() {
  const [job, setJob] = useState({ jobTitle: '', description: '', location: '', skills: '' });

  const submit = async (e) => {
    e.preventDefault();
    try {
      await api.post('/api/jobs/post', job);
      alert('Job posted');
      setJob({ jobTitle:'', description:'', location:'', skills:'' });
    } catch (err) {
      console.error(err);
      alert('Post failed');
    }
  };

  return (
    <div style={{ maxWidth:600 }}>
      <h2>Post Job</h2>
      <form onSubmit={submit}>
        <div><input placeholder="Job title" value={job.jobTitle} onChange={e => setJob({...job, jobTitle: e.target.value})} required /></div>
        <div style={{ marginTop:8 }}><input placeholder="Location" value={job.location} onChange={e => setJob({...job, location: e.target.value})} /></div>
        <div style={{ marginTop:8 }}><input placeholder="Skills (comma separated)" value={job.skills} onChange={e => setJob({...job, skills: e.target.value})} /></div>
        <div style={{ marginTop:8 }}><textarea placeholder="Description" value={job.description} onChange={e => setJob({...job, description: e.target.value})} /></div>
        <div style={{ marginTop:12 }}><button type="submit">Post Job</button></div>
      </form>
    </div>
  );
}
